import { defineCollection, z } from 'astro:content'

const books = defineCollection({
    type: 'content',
    schema: z.object({
        name: z.string(),
        author: z.string(),
        url: z.string().url(),
        cover: z.string().url(),
        description: z.string(),
        kind: z.string(),
        date: z.coerce.date().optional(),
        draft: z.boolean().default(false),
    }),
})

