const work = defineCollection({
    type: 'content',
    schema: z.object({
        title: z.string(),
        company: z.string(),
        from: z.coerce.date(),
        to: z.coerce.date().optional(),
        stack: z.string().transform((value) => value.split(' ')),
        logo: z.string().url(),
    }),
})

export const collections = { books, work }

