        publication(host: "alexwhiteside.dev/blog") {
            post(slug: $slug) {
                slug
                publishedAt
                readTimeInMinutes
                canonicalUrl
                title
                subtitle
                brief
                tags {
                    name
                    slug
                }
                url
                reactionCount
                content {
                    html
                }
                features {
                    tableOfContents {
                        items {
                            id
                            level
                            slug
                            title
